hudson.tasks.Shell.command:
export STC_PRIVATE_INSTALL_DIR=`pwd`/${STC_INSTALL_DIR}
cd ${STC_INSTALL_DIR}
echo "source SpirentTestCenter.tcl" >> reboot.tcl echo "stc::get system1" >> reboot.tcl echo "set hPhysicalChassisManager [stc::get system1 -children-physicalchassismanager]" >> reboot.tcl echo "stc::connect ${STC_CHASSIS_IP}" >> reboot.tcl echo "set hPhysicalChassis [stc::get \$hPhysicalChassisManager -children-physicalchassis]" >> reboot.tcl echo "set hTestModule [lindex [stc::get \$hPhysicalChassis -children-physicaltestmodule] ${STC_SLOT_ID}]" >> reboot.tcl echo "stc::perform RebootEquipment -EquipmentList \$hTestModule" >> reboot.tcl echo "exit" >> reboot.tcl
tclsh ./reboot.tcl
